#!/usr/bin/env node
/**
 * Dotfiles configurator.
 *
 * @since 1.0.0
 *
 * @note PLEASE DO NOT EDIT THIS FILE!
 * This entire file will be updated automatically.
 * - Instead of editing here, please review <https://github.com/clevercanyon/skeleton>.
 */
/* eslint-env es2021, node */

import desm     from 'desm';
import path     from 'path';
import fs       from 'fs-extra';
import { argv } from 'yargs';

( async () => {
	/**
	 * Initializes vars.
	 */
	const __dirname = desm( import.meta.url );
	const projDir   = path.resolve( __dirname, '../../..' );
	const pkg       = await fs.readJson( path.resolve( projDir, './package.json' ) );

	/**
	 * Configures dotfiles.
	 *
	 * @todo Needs completion.
	 */
	switch ( argv._[ 0 ] || '' ) {
		case 'cfw':
			break;

		case 'node':
			break;

		case 'web':
			delete pkg.main;
			pkg.browser = '';
			break;

		case 'any':
		default:
			break;
	}
} )();
